import React from 'react'

// 1. 부모 컴포넌트에서 전달받을 props들 인자로 넣어주기
function Hello({ color, name, isSpecial }) {
    return(
        <>
            <div style={{ color }}>
                {
                    isSpecial === true ? <b>넌 스페셜~~</b> : null
                }
                안녕하세요 {name}님!
            </div> 
        </>
    )
}

Hello.defaultProps = {
    name: '닉네임 없음'
}

export default Hello;
